import 'package:flutter/material.dart';

void main() {
  runApp(MyApp());
}

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      home: ItemListScreen(),
    );
  }
}

class ItemListScreen extends StatelessWidget {
  final List<Item> items = [
    Item(
      'Academia',
      'Treino de musculação focado em ganho de força e resistência. Ótimo para desenvolver músculos e aumentar a energia.',
      'data:image/jpeg;base64,',
    ),
    Item(
      'Ciclismo',
      'Pedalada ao ar livre ou indoor, excelente para melhorar o condicionamento cardiovascular, fortalecer as pernas e queimar calorias.',
      'data:image/jpeg;base64,',
    ),
    Item(
      'Natação',
      'Atividade aquática que trabalha todos os grupos musculares, melhora a capacidade respiratória e ajuda a manter o corpo em forma.',
      'data:image/jpeg;base64,',
    ),
    Item(
      'Caminhada',
      'Exercício de baixo impacto, perfeito para melhorar a saúde cardiovascular, fortalecer as pernas e aliviar o estresse.',
      'data:image/jpeg;base64,',
    ),
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(title: Text('StartLive - Exercises')),
      body: ListView.builder(
        itemCount: items.length,
        itemBuilder: (context, index) {
          return ListTile(
            title: Text(items[index].title),
            leading: ClipOval(
              child: Image.network(
                items[index].imagePath,
                width: 40,
                height: 40,
                fit: BoxFit.cover,
              ),
            ),
            onTap: () {
              Navigator.push(
                context,
                MaterialPageRoute(
                  builder: (context) => ItemDetailScreen(item: items[index]),
                ),
              );
            },
          );
        },
      ),
    );
  }
}

class ItemDetailScreen extends StatelessWidget {
  final Item item;

  ItemDetailScreen({required this.item});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(title: Text('Detalhes do Exercício')),
      body: Center(
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: <Widget>[
            Image.network(
              item.imagePath,
              width: 40,
              height: 40,
              fit: BoxFit.cover,
            ),
            SizedBox(height: 30),
            Text(item.title,
                style: TextStyle(fontSize: 20, fontWeight: FontWeight.bold)),
            Padding(
              padding: const EdgeInsets.all(30),
              child: Text(item.description,
                  style: TextStyle(fontSize: 16), textAlign: TextAlign.center),
            ),
          ],
        ),
      ),
    );
  }
}

class Item {
  final String title;
  final String description;
  final String imagePath;

  Item(this.title, this.description, this.imagePath);
}
